var campoFiltro=document.querySelector("#filtrar-tabela");campoFiltro.addEventListener("input",function(){var e=document.querySelectorAll(".paciente");if(0<this.value.length)for(var t=0;t<e.length;t++){var i=(l=e[t]).querySelector(".info-nome").textContent;new RegExp(this.value,"i").test(i)?l.classList.remove("invisivel"):l.classList.add("invisivel")}else for(t=0;t<e.length;t++){var l;(l=e[t]).classList.remove("invisivel")}});